```go
package main

import (
	"fmt"
	"golang.org/x/net/html"
	"io/ioutil"
	"net/http"
	"strings"
)

func main() {
	url := "https://www.phrases.org.uk/meanings/skeleton-in-the-closet.html"
	resp, err := http.Get(url)
	if err != nil {
		panic(err)
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		panic(err)
	}

	doc, err := html.Parse(strings.NewReader(string(body)))
	if err != nil {
		panic(err)
	}

	var origin, people string
	var f func(*html.Node)
	f = func(n *html.Node) {
		if n.Type == html.ElementNode && n.Data == "div" {
			for _, a := range n.Attr {
				if a.Key == "id" && (a.Val == "origin" || a.Val == "famous-people") {
					content := strings.TrimSpace(n.FirstChild.Data)
					if a.Val == "origin" {
						origin = content
					} else {
						people = content
					}
				}
			}
		}
		for c := n.FirstChild; c != nil; c = c.NextSibling {
			f(c)
		}
	}
	f(doc)

	fmt.Println("Origin:", origin)
	fmt.Println("Famous People:", people)
}
```

