from .models import FittedModel, WalkForward, FittedModelWalkForward

class FittedModelTypeFilter(admin.SimpleListFilter):
    title = 'Type'
    parameter_name = 'type'

    def lookups(self, request, model_admin):
        return FittedModel.Type.choices

    def queryset(self, request, queryset):
        if self.value():
            return queryset.filter(type=self.value())
        return queryset

class WalkForwardDateRangeFilter(admin.SimpleListFilter):
    title = 'Test date range'
    parameter_name = 'test_date_range'

    def lookups(self, request, model_admin):
        return (
            ('last_month', 'Last month'),
            ('last_year', 'Last year'),
        )

    def queryset(self, request, queryset):
        from datetime import datetime, timedelta
        now = datetime.now()
        if self.value() == 'last_month':
            last_month = now - timedelta(days=30)
            return queryset.filter(test_start__gte=last_month, test_end__lte=now)
        elif self.value() == 'last_year':
            last_year = now - timedelta(days=365)
            return queryset.filter(test_start__gte=last_year, test_end__lte=now)
        return queryset

@admin.register(FittedModel)
